function createRobotCard(robot) {
    const card = document.createElement('div');
    card.classList.add('robot-card');
    
    card.innerHTML = `
        <div class="robot-img">
            <img src="${robot.image}" alt="${robot.name}">
            <div class="robot-status"></div>
        </div>
        <div class="robot-info">
            <h2>${robot.name}</h2>
            <p class="username">@${robot.username}</p>
            <p>${robot.email}</p>
        </div>
    `;
    
    card.addEventListener('mouseenter', () => {
        const hoverSound = new Audio('data:audio/wav;base64,');
        hoverSound.volume = 0.2;
        hoverSound.play().catch(e => console.log("Audio play prevented by browser"));
    });
    
    return card;
}

function displayRobots(robotsToDisplay) {
    const container = document.getElementById('robotContainer');
    container.innerHTML = '';
    
    if (robotsToDisplay.length === 0) {
        const noResults = document.createElement('div');
        noResults.classList.add('no-results');
        noResults.innerHTML = 'SUBJECT <span>NOT FOUND</span> IN DATABASE';
        container.appendChild(noResults);
        return;
    }
    
    robotsToDisplay.forEach(robot => {
        const card = createRobotCard(robot);
        container.appendChild(card);
    
        setTimeout(() => {
            card.style.opacity = '1';
        }, Math.random() * 500);
    });
}

function filterRobots(searchText) {
    return robots.filter(robot => 
        robot.name.toLowerCase().includes(searchText.toLowerCase())
    );
}

document.addEventListener('DOMContentLoaded', () => {
    setTimeout(() => {
        displayRobots(robots);
    }, 300);
    
    const searchInput = document.getElementById('searchInput');
    searchInput.addEventListener('input', (e) => {
        const keySound = new Audio('data:audio/wav;base64,');
        keySound.volume = 0.05;
        keySound.play().catch(e => console.log("Audio play prevented by browser"));
        
        const filteredRobots = filterRobots(e.target.value);
        displayRobots(filteredRobots);
    });
    
    searchInput.focus();
    
    setInterval(() => {
        if (Math.random() > 0.95) {
            document.body.classList.add('glitch');
            setTimeout(() => {
                document.body.classList.remove('glitch');
            }, 200);
        }
    }, 3000);
});